import React, { Component } from 'react'
import { Button, Image, StyleSheet, Text, View } from 'react-native'
import { TouchableHighlight } from 'react-native-gesture-handler';

export default class Perfil extends Component {
    render() {
        let pic = {
            uri: 'https://i.pinimg.com/originals/12/7e/59/127e591e15fd44f633c1570306653884.jpg'
        };

        return (
            <View style={styles.container}>
                <Image  source={pic} style={styles.imagem} />

                <View style={styles.content}>
                    <Text style={styles.contentText}>Nome: {this.props.route.params.nome}</Text>
                    <Text style={styles.contentText}>Idade: {this.props.route.params.idade}</Text>
                    <Text style={styles.contentText}>Email: {this.props.route.params.email}</Text>
                </View>
                <TouchableHighlight 
                    onPress={
                        () => this.props.navigation.navigate('Home')}
                >
                    <View style={styles.button}>
                        <Text style={styles.buttonText}>HOME</Text>
                    </View>
                </TouchableHighlight>
            </View>)
    }
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#ffe6c7',
        alignItems: 'center',
        justifyContent: 'center',
    },

    content: {
        padding: 20,
        alignContent: 'flex-start'
    },

    contentText: {
        color: '#fa7921',
        fontSize: 20,
        fontWeight: "bold",
        textAlign: 'justify'
    },

    button: {
        padding: 5,
        width: 150,
        margin: 5,
        borderRadius: 10,
        alignItems: 'center',
        backgroundColor: '#fa7921',
    },

    buttonText: {
        color: '#fff',
        padding: 5,
        fontSize: 18,
        fontWeight: "bold",
    },

    imagem: {
        width: 203, 
        height: 200,
        marginBottom: 15,
        borderRadius: 10,
        borderColor: 'gray',
        borderWidth: 5,
    }
});
